import React, { useEffect, useState } from 'react';
import { Button, Col, Row, Card, Badge, Accordion } from 'react-bootstrap';
import MainScreen from '../../components/MainScreen/MainScreen';
import axios from 'axios';
import { useDispatch, useSelector } from "react-redux";
import { listaRestaurantes } from "../../actions/restaurantesActions";
import ErrorMessage from "../../components/ErrorMessage";

const Restaurantes = () => {
    const dispatch = useDispatch();

    const listaRestaurantes = useSelector((state) => state.listaRestaurantes);
    const { error, restaurantes } = listaRestaurantes;

    useEffect(() => {
        dispatch(listaRestaurantes());
    }, [dispatch])

    //const [restaurantes, setRestaurantes] = useState([]);

    /*const fetchRestaurantes = async () => {
        const { data } = await axios.get('/api/restaurantes/');
        setRestaurantes(data);
    }

    useEffect(() => {
        fetchRestaurantes();
    }, [])*/

