import React, { useEffect, useState } from 'react';
import MainScreen from '../../components/MainScreen/MainScreen';
import { Col, Form, Row, Button, Table } from 'react-bootstrap';
import axios from 'axios';

const Productos = () => {

    //Obtiene productos de la base de datos
    const [Productos, setProductos] = useState([]);
    useEffect(() => {
        const tablaQuery = async () => {
            const { data } = await axios.get("/api/productos/");
            setProductos(data);
        }
        tablaQuery();
    });

    return (
        <MainScreen title="Productos">
            <Row>
                <Col>
                    Formulario para AÃ±adir Productos
                    <Form>
                        hola
                    </Form>
                </Col>

                <Col>
                    Reporte de Productos
                    <Table striped bordered hover size="sm">
                        <thead>
                            <tr className='bg-primary text-warning'>
                                <th>Producto</th>
                                <th>Para Venta</th>
                                <th>Precio</th>
                            </tr>
                        </thead>
                        <tbody>
                            {Productos.map(Productos => (
                                <tr key={Productos._id}>
                                    <td>{Productos.prodNombre}</td>
                                    <td>{Productos.categoria}</td>
                                    <td>{Productos.precio}</td>
                                </tr>
                            ))}
                        </tbody>
                    </Table>
                </Col>
            </Row>
        </MainScreen>
    )
}

export default Productos
