import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { Button, Col, Form, Row } from 'react-bootstrap';
import MainScreen from '../../components/MainScreen/MainScreen';

const Tareas = () => {

    //Valores para el Query a FACTURA
    const [facProducto, setFacProducto] = useState("");
    const [precioUnit, setPrecioUnit] = useState(0);
    const [cantidad, setCantidad] = useState(0);
    const [precioTotal, setPrecioTotal] = useState(0);


    //Obtiene Clientes
    const [clientes, setClientes] = useState([]);
    useEffect(() => {
        const clientesSelect = async () => {
            const config = { headers: { "Content-type": "application/json" } };
            const { data } = await axios.get("/api/clientes/", config);
            setClientes(data);
        }
        clientesSelect();
    });

    //Obtiene Productos
    const [productos, setProductos] = useState([]);
    useEffect(() => {
        const productosSelect = async () => {
            const config = { headers: { "Content-type": "application/json" } };
            const { data } = await axios.get("/api/productos/", config);
            setProductos(data);
        }
        productosSelect();
    });

    //Submit producto a factura
    const submitProducto = async (e) => {
        e.preventDefault();
        try {
            const config = { headers: { "Content-type": "application/json" } };
            const { data } = await axios.post("/api/factura",
                { facProducto, precioUnit, cantidad, precioTotal }, config);

        } catch (error) {

        }
    }


    return (
        <MainScreen title="clientes y facturacion">
            <Row>

                <a href='/empleado/cliente' className='mb-5'>
                    <Button variant='primary' className="rounded text-warning">
                        Agregar Cliente +
                    </Button>
                </a>

                <Col >
                    <Form onSubmit={submitProducto}>
                        <Form.Label>Clientes</Form.Label>
                        <Form.Select value={clientes}>
                            <option></option>
                            {clientes?.map(clientes => (
                                <option key={clientes._id}>{clientes.nombre}</option>
                            ))}
                        </Form.Select>

                        <Form.Label className="mt-3">Productos</Form.Label>
                        <Form.Select>
                            <option></option>
                            {productos?.map(productos => (
                                <option key={productos._id}>{productos.prodNombre}</option>
                            ))}
                        </Form.Select>


                        <Form.Label className='mt-3'>Cantidad</Form.Label>
                        <Form.Control required
                            type="number"
                            value={cantidad}
                            onChange={(e) => setCantidad(e.target.value)}
                        />

                        <Button variant="primary" className="text-warning mt-4" type="submit">
                            Agregar a la factura
                        </Button>
                    </Form>
                </Col>

                <Col>
                    aa
                </Col>

            </Row>
        </MainScreen>

    )
}

export default Tareas
