import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { Row, Button, Table, Col, Form } from 'react-bootstrap';
import MainScreen from '../../components/MainScreen/MainScreen';

const Tareas = () => {

    /* Rellenar Select PRODUCTOS */
    const [productos, setProductos] = useState([]);
    const [cantidad, setCantidad] = useState("");

    useEffect(() => {
        const fetchProductos = async () => {
            const config = { headers: { "Content-type": "application/json" } };
            const { data } = await axios.get("/api/productos", config);
            setProductos(data);
        }
        fetchProductos();
    });

    /* Rellenar tabla Factura */
    const [facturas, setFacturas] = useState([]);
    useEffect(() => {
        const fetchFacturas = async () => {
            const { data } = await axios.get('/api/facturas');
            setFacturas(data);
        }
        fetchFacturas();
    });


    return (
        <MainScreen title="Facturación y clientes">
            <Row className="mb-4">
                <Col>
                    <a href='/empleado/cliente'>
                        <Button variant='primary' className="rounded text-warning">
                            Agregar Cliente +
                        </Button>
                    </a>
                </Col>
            </Row>

            <Row>
                <Col>
                    <Row>
                        <Form>

                            <Col>
                                Productos
                                <Table striped bordered hover size="sm">
                                    <thead>
                                        <tr>
                                            <th className='bg-primary text-info'>Nombre</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {productos.map(productos => (
                                            <tr key={productos._id} >
                                                <td>{productos.prodNombre}</td>
                                            </tr>
                                        ))}
                                    </tbody>
                                </Table>

                                <Form.Group controlId="cantidad" className='mb-4'>
                                    <Form.Label>Cantidad</Form.Label>
                                    <Form.Control required
                                        type="number"
                                        placeholder="Cantidad del producto adquirido"
                                        value={cantidad}
                                        onChange={(e) => setCantidad(e.target.value)}
                                    />
                                </Form.Group>

                                <Button variant="primary" className="text-warning" type="submit">
                                    Agregar elemento a la factura
                                </Button>
                            </Col>
                        </Form>
                    </Row>
                </Col>



                <Col>
                    Factura
                    <Table striped bordered hover size="sm">
                        <thead>
                            <tr>
                                <th>Producto</th>
                                <th>Cantidad</th>
                                <th>Precio Unitario</th>
                                <th>Precio</th>
                            </tr>
                        </thead>
                        <tbody>
                            {facturas.map(facturas => (
                                <tr key={facturas._id} >
                                    <td>{facturas.facProducto}</td>
                                    <td>{facturas.cantidad}</td>
                                    <td>{facturas.precioUnit}</td>
                                    <td>{facturas.precioTotal}</td>
                                </tr>
                            ))}
                        </tbody>
                    </Table>


                </Col>

            </Row>

        </MainScreen >
    )
}

export default Tareas
