import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { Row, Button, Table, Col, Form } from 'react-bootstrap';
import MainScreen from '../../components/MainScreen/MainScreen';

const Cliente = () => {

    const [clientes, setClientes] = useState([]);
    useEffect(() => {
        const fetchClientes = async () => {
            const { data } = await axios.get('/api/clientes/');
            setClientes(data);
        }
        fetchClientes();
    })




    return (
        <MainScreen title="ventana de clientes">
            <Row>
                <Col>
                    <Form>
                        <Form.Group controlId="nombre" className='mb-4'>
                            <Form.Label>Nombre</Form.Label>
                            <Form.Control required
                                type="text"
                                placeholder="Nombre del cliente"
                            //value={name}
                            //onChange={(e) => setName(e.target.value)}
                            />
                        </Form.Group>
                        <Form.Group controlId="email" className='mb-4'>
                            <Form.Label>Email</Form.Label>
                            <Form.Control required
                                type="email"
                                placeholder="Ingrese correo del cliente"
                            //value={name}
                            //onChange={(e) => setName(e.target.value)}
                            />
                        </Form.Group>
                        <Form.Group controlId="tel" className='mb-4'>
                            <Form.Label>Teléfono</Form.Label>
                            <Form.Control required
                                type="text"
                                placeholder="Ingrese el número telefónico del cliente"
                            //value={name}
                            //onChange={(e) => setName(e.target.value)}
                            />
                        </Form.Group>
                    </Form>
                </Col>
                <Col>
                    <Table striped bordered hover size="sm">
                        <thead>
                            <tr>
                                <th>Nombre</th>
                                <th>Email</th>
                                <th>Tel</th>
                            </tr>
                        </thead>
                        <tbody>
                            {clientes.map(clientes => (
                                <tr>
                                    <td>{clientes.nombre}</td>
                                    <td>{clientes.email}</td>
                                    <td>{clientes.tel}</td>
                                </tr>
                            ))}
                        </tbody>
                    </Table>
                </Col>
            </Row>
        </MainScreen>
    )
}

export default Cliente
