import React from 'react'
import { Button, Col, Row, Card, Badge } from 'react-bootstrap';
import MainScreen from '../../components/MainScreen/MainScreen';
import jsonTest from '../../data/jsonTest';

const Restaurantes = () => {

    const deleteHandler = (id) => {
