    };


    return (
        <MainScreen title='Admin - Restaurantes'>

            <Row>
                <Col>
                    <a href='/tareas'>
                        <Button variant='primary' className="rounded text-warning">
                            Agregar Restaurante +
                        </Button>
                    </a>
                </Col>
            </Row>

            <Row className='mt-5'>

                {
                    jsonTest.map(restaurantes => (

                        <Card style={{ margin: 10 }} bg="light">
                            <Card.Header style={{ display: "flex" }}>
                                <span
                                    style={{
                                        color: "black",
                                        textDecoration: "none",
                                        flex: 1,
                                        cursor: "pointer",
                                        alignSelf: "center",
                                        fontSize: 18,
                                    }}>
                                    {restaurantes.title}
                                </span>
                                <div>
                                    <Button href={`/admin/restaurantes/${restaurantes._id}`} variant="primary" className='text-warning rounded'>Editar</Button>
                                    <Button
                                        variant="primary"
                                        className='mx-2  text-danger rounded'
                                        onClick={() => deleteHandler(restaurantes._id)}
                                    >Eliminar</Button>
                                </div>
                            </Card.Header>

                            <Card.Body>
                                <h4>
                                    <Badge bg="info" style={{ fontSize: 13 }}>Estado - {restaurantes.category}</Badge>
                                </h4>
                                <blockquote className="blockquote mb-0">
                                    <p>
                                        {restaurantes.content}
                                    </p>
                                    <footer className="blockquote-footer" style={{ fontSize: 13 }}>
                                        Creado el día - FECHA
                                    </footer>
                                </blockquote>
                            </Card.Body>

                        </Card>

                    ))
                }


            </Row>

        </MainScreen >
    )
}

export default Restaurantes
