import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { Row, Button, Table } from 'react-bootstrap';
import MainScreen from '../../components/MainScreen/MainScreen';


const Tareas = () => {

    const contenido = localStorage.getItem("userInfo") ? JSON.parse(localStorage.getItem("userInfo")) : null;
    const [misRestaurantes, setMisRestaurantes] = useState([]);

    useEffect(() => {
        const fetchRestaurantes = async () => {
            const { data } = await axios.get('/api/restaurantes/');
            setMisRestaurantes(data);
        }
        fetchRestaurantes();
    })

    const deleteHandler = async (id) => {
        /* if (window.confirm("¿Está seguro de eliminar el usuario seleccionado?")) {
             const config = { headers: { "Content-type": "application/json" } };
             const { data } = await axios.delete(`/api/users/${id}`, config);
             console.log(data);
             window.location.replace("/admin/usuarios");
         }*/
    };

    return (
        <MainScreen title="Restaurantes que administro">

            <Row className='mt-5'>
                <Table striped bordered hover size="sm">
                    <thead>
                        <tr>
                            <th>Nombre</th>
                            <th>Email</th>
                            <th>Estado</th>
                            <th>Rol</th>
                            <th>Restaurante Asignado</th>
                            <th>Salario</th>
                            <th>Acciones</th>
                        </tr>
                    </thead>
                    <tbody>
                        {misRestaurantes.map(misRestaurantes => (

                            <tr key={misRestaurantes._id}>
                                <td>{misRestaurantes.nombreRestaurante}</td>
                                <td>{misRestaurantes.emailRestaurante}</td>
                                <td>{misRestaurantes.direccionFisica}</td>
                                <td>{misRestaurantes.emailRestaurante}</td>
                                <td>{misRestaurantes.telRestaurante}</td>
                                <td>{misRestaurantes.gerenteAsignado}</td>
                                <td className>
                                    <Button href={`/admin/usuario/${misRestaurantes._id}`}
                                        variant="primary" className='text-warning rounded'>Contratar</Button>
                                </td>
                            </tr>

                        ))}
                    </tbody>
                </Table>
            </Row>
        </MainScreen>
    )
}

export default Tareas
